import React from 'react';
import {
  GroupContainer,
  FormInputContainer,
  FormInputLabelContainer,
} from './form-input.styles';
const FormInput = ({ handleChange, label, ...otherProps }) => {
  return (
    <GroupContainer>
      <FormInputContainer
        onChange={handleChange}
        {...otherProps}
      />
      {label ? (
        <FormInputLabelContainer
          className={otherProps.value.length ? 'shrink' : ''}
        >
          {label}
        </FormInputLabelContainer>
      ) : null}
    </GroupContainer>
  );
};

export default FormInput;
