    subtitle: 'SHOP NOW!',
  };
  return (
    <div className='menu-item'>
      <div className='content'>
        {() =>
          this.state.title.map((title) => {
            return <h1 className='title' key={strResult.id}>{title}</h1>;
          })
        }

        <span className='subtitle'>{this.state.subtitle}</span>
      </div>
    </div>
  );
};

export default MenuItem;
