import React, { useState } from 'react';
import '../menu-item/menu-item.styles.scss';

const MenuItem = () => {
  const [title, setTitle] = useState(
    'HATS',
    'JACKETS',
    'SNEAKERS',
    'WOMENS',
    'MENS'
  );
  const [subtitle, setSubtitle] = useState('SHOP NOW!');
  return (
    <div className='menu-item'>
      <div className='content'>
        {this.state.title.map((ttl) => {
          return (
            <h1 className='title' key={ttl}>
              {ttl}
            </h1>
          );
        })}
        <span className='subtitle'>{this.props.sbttl}</span>
      </div>
    </div>
  );
};

export default MenuItem;
